mapper.select:



SELECT a0.*,u2.id as creator,u2.username as creatorName,r.id AS roleId,r.name roleName
FROM (
    select u1.*
    from
    (
      SELECT u0.*,rownum as n
      FROM
      (
        SELECT *
        FROM admins

		ORDER BY ${sort} ${order}
      ) u0
      WHERE rownum <=#{end}
    ) u1
    where u1.n >= #{start}
) a0 ,ROLES r,admins u2,ADMINS_ROLES ar
WHERE a0.creator = u2.id(+) AND  a0.id = ar.ADMIN(+) AND r.id(+) = ar.ROLE
